package day02;

public class Ex04_int {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		8진수 : 0?
//		16진수 : 0x?
		int  var1 = -10;
		System.out.println("var1 : " + var1);
		int  var2 = -010;
		System.out.println("var2 : " + var2);
		int  var3 = 0x10;
		System.out.println("var3 : " + var3);
		int  var4 = 0b1100;
		System.out.println("var4 : " + var4);
	}

}
