package day02;

public class Ex06_FloatDouble {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 실수 타입 리터럴
		// float 타입 리터럴 : 실수F
		// double 타입 리터럴 : 실수D
		// double 타입 리터럴 : D 생략가능
		double ln1 = 3.14;
		double ln2 = 3.14D;
		float ln3 = 3.14F;
		
		double ln4= 0.123456789123456789D;
		float ln5=0.123456789123456789F;

		System.out.println(ln1);
		System.out.println(ln2);
		System.out.println(ln3);
		System.out.println(ln4);
		System.out.println(ln5);
	}

}
